const result = {};

for (const item of items) {
  const buyer = item.json["Buyer name "] || item.json["Buyer name"]; 
  const inspected = Number(item.json["Inspected qty "]) || Number(item.json["Inspected qty"]) || 0;
  const defectRaw = item.json["Defect type "] || item.json["Defect type"] || "";

  if (buyer) {
    const buyerName = buyer.trim();
    if (!result[buyerName]) {
      result[buyerName] = {
        buyer: buyerName,
        total_inspected: 0,
        total_defect: 0,
        defects: {}
      };
    }

    result[buyerName].total_inspected += inspected;

    // Regex update: Bracket er vitorer songkhya ebong comma handle korar jonno
    const defectRegex = /([a-zA-Z\s/-]+)\s*\((\d+)\)/g;
    let match;

    while ((match = defectRegex.exec(defectRaw)) !== null) {
      // match[1] hosse defect er naam, match[2] hosse bracket er vitorer songkhya
      let typeName = match[1].trim(); 

      // Jodi name er shuru ba sheshe comma thake seta remove korbe
      typeName = typeName.replace(/^,+|,+$/g, '').trim();

      const typeCount = parseInt(match[2]) || 0; 

      result[buyerName].total_defect += typeCount;
      result[buyerName].defects[typeName] = (result[buyerName].defects[typeName] || 0) + typeCount;
    }
  }
}

return Object.values(result).map(r => {
  r.defect_percent = r.total_inspected > 0 
    ? Number(((r.total_defect / r.total_inspected) * 100).toFixed(2)) 
    : 0;
  return { json: r };
});

// Apnar process kora data ekhane thakbe
const lines = items.map(item => item.json);

// Shob unique defect type ber kora (Row toiri korar jonno)
const allDefectTypes = [...new Set(lines.flatMap(l => Object.keys(l.defects)))];

// Vertical HTML Table toiri kora
let html = `
<style>
  table { 
    border-collapse: collapse; 
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; 
    width: 100%; 
    max-width: 800px; 
    margin: 20px auto; 
    box-shadow: 0 0 20px rgba(0,0,0,0.1); 
  }
  td, th { border: 1px solid #333; padding: 12px; text-align: center; }
  .label { 
    font-weight: bold; 
    background-color: #f8f9fa; 
    text-align: left; 
    width: 150px; 
    color: #333; 
  }
  .header-row td { 
    background-color: #007bff; 
    color: white; 
    font-weight: bold; 
    font-size: 1.1em; 
  }
  .percent-cell { color: #d9534f; font-weight: bold; }
  .summary-header { 
    background-color: #e9ecef; 
    text-align: center; 
    font-weight: bold; 
    text-transform: uppercase;
  }
</style>

<table>
  <tr class="header-row">
    <td class="label" style="background-color: #f8f9fa; color: #333;">Buyer Name</td>
    ${lines.map(l => `<td>${l.buyer || 'N/A'}</td>`).join('')}
  </tr>
  <tr>
    <td class="label">Inspected Qty</td>
    ${lines.map(l => `<td>${l.total_inspected}</td>`).join('')}
  </tr>
  <tr>
    <td class="label">Defected Qty</td>
    ${lines.map(l => `<td>${l.total_defect}</td>`).join('')}
  </tr>
  <tr>
    <td class="label">Defect Percent</td>
    ${lines.map(l => `<td class="percent-cell">${l.defect_percent}%</td>`).join('')}
  </tr>

  <tr>
    <td class="label summary-header" colspan="${lines.length + 1}">Defect Breakdown</td>
  </tr>

  ${allDefectTypes.map(type => `
    <tr>
      <td class="label">${type}</td>
      ${lines.map(l => `<td>${l.defects[type] || '0'}</td>`).join('')}
    </tr>
  `).join('')}
</table>`;

return [{ json: { my_html: html } }];

// PDF node theke asha binary data dhora hochche
let binaryData = items[0].binary.data; 

// Bangladesh Time (Asia/Dhaka) onujayi date ebong time toiri kora
const now = new Date();

// Date: DD-MM-YYYY format (Bangladesh Timezone)
const dateStr = now.toLocaleDateString('en-GB', { 
  timeZone: 'Asia/Dhaka' 
}).replace(/\//g, '-'); 

// Time: 06-30 PM format (Bangladesh Timezone)
const timeOptions = { 
  hour: '2-digit', 
  minute: '2-digit', 
  hour12: true, 
  timeZone: 'Asia/Dhaka' 
};
let timeStr = now.toLocaleTimeString('en-US', timeOptions).replace(':', '-'); 

// Final Name: QC_report_12-01-2026_(06-30_PM).pdf
binaryData.fileName = `HOURLY_BUYER_WISE_report_${dateStr}_(${timeStr}).pdf`;

return items;
