const now = new Date();
const oneHourAgo = new Date(now.getTime() - (60 * 60 * 1000));

// Filter logic
return items.filter(item => {
  // Apnar data onujayi "Submitted at" column bebohar kora hoyeche
  const rowTime = new Date(item.json["Submitted at"]);

  // Jodi time thik moto na thake (Invalid Date) tobe bad jabe
  if (isNaN(rowTime)) return false;

  // Kebol golo 1 ghontar moddher data return hobe
  return rowTime >= oneHourAgo && rowTime <= now;
});

const result = {};

items.forEach(item => {
  // Key name check kora (extra space handle korar jonno)
  const line = item.json["Line no"] || item.json["Line no "];
  const inspected = Number(item.json["Inspected qty "]) || Number(item.json["Inspected qty"]) || 0;
  const defectRaw = item.json["Defect type "] || item.json["Defect type"] || "";

  if (line !== undefined && line !== null) {
    if (!result[line]) {
      result[line] = {
        line: line,
        total_inspected: 0,
        total_defect: 0,
        defects: {}
      };
    }

    result[line].total_inspected += inspected;

    // ১. কমা, সেমিকোলন বা নিউ লাইন দিয়ে আলাদা করা
    const defectLines = defectRaw.split(/[\n,;]+/); 

    defectLines.forEach(singleDefect => {
      // ২. "Open seam(1)" অথবা "Hole 5" - উভয় ফরম্যাট রিড করার জন্য Regex
      // ([^()0-9,]+) -> সংখ্যা বা ব্র্যাকেট ছাড়া সব লেখা নেবে
      // \(?(\d+)\)? -> ব্র্যাকেটের ভেতরে বা বাইরে থাকা সংখ্যা নেবে
      const match = singleDefect.match(/([^()0-9,]+)\s*\(?(\d+)\)?/);

      if (match) {
        const typeName = match[1].trim(); 
        const typeCount = parseInt(match[2]) || 0; 

        if (typeName) {
          result[line].total_defect += typeCount;
          result[line].defects[typeName] = (result[line].defects[typeName] || 0) + typeCount;
        }
      }
    });
  }
});

return Object.values(result).map(r => {
  r.defect_percent = r.total_inspected > 0 
    ? Number(((r.total_defect / r.total_inspected) * 100).toFixed(2)) 
    : 0;
  return { json: r };
});

// Apnar dewa process kora data ekhane input hisebe thakbe
const lines = items.map(item => item.json);

// Shob unique defect type ber kora row toiri korar jonno
const allDefectTypes = [...new Set(lines.flatMap(l => Object.keys(l.defects)))];

// Vertical HTML Table toiri kora
let html = `
<style>
  table { border-collapse: collapse; font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; min-width: 450px; margin: 20px auto; box-shadow: 0 0 20px rgba(0,0,0,0.1); }
  td, th { border: 1px solid #333; padding: 12px; text-align: center; }
  .label { font-weight: bold; background-color: #f8f9fa; text-align: left; width: 160px; color: #333; }
  .header-row td { background-color: #ffffff; font-weight: bold; font-size: 1.1em; }
  .percent-cell { color: #d9534f; font-weight: bold; }
</style>
<table>
  <tr class="header-row">
    <td class="label"></td>
    ${lines.map(l => `<td>line ${l.line}</td>`).join('')}
  </tr>
  <tr>
    <td class="label">inspected qty</td>
    ${lines.map(l => `<td>${l.total_inspected}</td>`).join('')}
  </tr>
  <tr>
    <td class="label">defected qty</td>
    ${lines.map(l => `<td>${l.total_defect}</td>`).join('')}
  </tr>
  <tr>
    <td class="label">defect percent</td>
    ${lines.map(l => `<td class="percent-cell">${l.defect_percent}%</td>`).join('')}
  </tr>
  <tr>
    <td class="label" colspan="${lines.length + 1}" style="background-color: #e9ecef; text-align: center;">defect type :</td>
  </tr>
  ${allDefectTypes.map(type => `
    <tr>
      <td class="label">${type}</td>
      ${lines.map(l => `<td>${l.defects[type] || '0'}</td>`).join('')}
    </tr>
  `).join('')}
</table>`;

return [{ json: { my_html: html } }];

// PDF node theke asha binary data dhora hochche
let binaryData = items[0].binary.data; 

// Bangladesh Time (Asia/Dhaka) onujayi date ebong time toiri kora
const now = new Date();

// Date: DD-MM-YYYY format (Bangladesh Timezone)
const dateStr = now.toLocaleDateString('en-GB', { 
  timeZone: 'Asia/Dhaka' 
}).replace(/\//g, '-'); 

// Time: 06-30 PM format (Bangladesh Timezone)
const timeOptions = { 
  hour: '2-digit', 
  minute: '2-digit', 
  hour12: true, 
  timeZone: 'Asia/Dhaka' 
};
let timeStr = now.toLocaleTimeString('en-US', timeOptions).replace(':', '-'); 

// Final Name: QC_report_12-01-2026_(06-30_PM).pdf
binaryData.fileName = `HOURLY_LINE_WISE_report_${dateStr}_(${timeStr}).pdf`;

return items;
